import React, { useState } from 'react';
import './style.css';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const openNav = () => {
    setSidebarOpen(true);
  };

  const closeNav = () => {
    setSidebarOpen(false);
  };

  return (
    <div>
      <header className="header">
        <div className="logo">CXLK</div>
        <nav>
          <ul>
            <li><a href="#">Ürünler</a></li>
            <li><a href="#">İletişim</a></li>
            <li><a href="#">Hakkımızda</a></li>
            <li><a href="#">Giriş Yap</a></li>
            <li><button className="menu-button" onClick={openNav}>☰ Menu</button></li>
          </ul>
        </nav>
      </header>
      <div className="video-section">
        <div className="video-background">
          <video autoPlay muted loop>
            <source src="/beehive.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
        <div className="video-overlay">
          <h1>Welcome to Our Website</h1>
          <p>Discover our products and services</p>
        </div>
      </div>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`} id="mySidebar">
        <a href="javascript:void(0)" className="closebtn" onClick={closeNav}>×</a>
        <div className="logo">CXLK</div>
        <a href="#">Ürünler</a>
        <a href="#">İletişim</a>
        <a href="#">Hakkımızda</a>
        <a href="#">Giriş Yap</a>
      </div>
    </div>
  );
}

export default App;
