import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPhone, faInfo, faRightToBracket, faHouse, } from '@fortawesome/free-solid-svg-icons';
import { faForumbee } from '@fortawesome/free-brands-svg-icons';
import './style.css';

function App() {
  const [activeTab, setActiveTab] = useState('anasayfa');

  return (
    <div>
      <header className="header">
        <div className="logo">Beeonhive</div>
        <nav>
          <ul className="headerbuton">
            <li
              className={activeTab === 'anasayfa' ? 'active' : ''}
              onClick={() => setActiveTab('anasayfa')}
            >
              <a href="#"><FontAwesomeIcon icon={faHouse} className='icon-small' />Ana Sayfa</a>
            </li>
            <li
              className={activeTab === 'urunler' ? 'active' : ''}
              onClick={() => setActiveTab('urunler')}
            >
              <a href="#"><FontAwesomeIcon icon={faForumbee} className='icon-small' />Ürünümüz</a>
            </li>
            
            <li
              className={activeTab === 'hakkimizda' ? 'active' : ''}
              onClick={() => setActiveTab('hakkimizda')}
            >
              <a href="#">
                <FontAwesomeIcon icon={faInfo} className="icon-small" /> Hakkımızda
              </a>
            </li>
            <li
              className={activeTab === 'iletisim' ? 'active' : ''}
              onClick={() => setActiveTab('iletisim')}
            >
              <a href="#">
                <FontAwesomeIcon icon={faPhone} className="icon-small" /> İletişim
              </a>
            </li>
            <li
              className={activeTab === 'giris' ? 'active' : ''}
              onClick={() => setActiveTab('giris')}
            >
              <a href="#"><FontAwesomeIcon icon={faRightToBracket} className="icon-small" />Giriş Yap</a>
            </li>
          </ul>
        </nav>
      </header>
      <div>
        <div className="video-container">
          <video autoPlay muted loop>
            <source src="/beehiveloop.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
        <div className="video-overlay">
          <h1>Welcome to Our Website</h1>
          <p>Discover our products and services</p>
        </div>
      </div>
    </div>
  );
}

export default App;
